INSERT INTO `t_news` VALUES ('6906', '2', '2', '11', '汇宝', '毛毛', '\n【证监会要求机构自查整改资管业务】据财新网，多地证监局近日向辖区内证券公司、基金公司及期货公司下发通知，要求开展私募资管业务自查整改，重点针对“保本”类和结构化资管计划产品，这可看作是监管层落实新“八条底线”的后续督导工作。', null, null, '\n<b><font color class=\"important-text\">【证监会要求机构自查整改资管业务】</font></b>据财新网，多地证监局近日向辖区内证券公司、基金公司及期货公司下发通知，要求开展私募资管业务自查整改，重点针对“保本”类和结构化资管计划产品，这可看作是监管层落实新“八条底线”的后续督导工作。', '1', '1', '2016-08-10 14:26:22', '1');
INSERT INTO `t_news` VALUES ('6907', '2', '2', '11', '汇宝', '毛毛', '\n【国家信息中心预计中国今年CPI上涨2.0% PPI下降2.7%】上海证券报刊登国家信息中心预测部的年中经济观察报告称，未来一段时期，总需求稳中趋缓、产能严重过剩、粮食产量和库存双高等因素将抑制物价涨幅；M1快速增长、劳动力成本上升、猪肉价格高位运行等因素将支撑价格上涨。', null, null, '\n<b>【国家信息中心预计中国今年CPI上涨2.0% PPI下降2.7%】</b>上海证券报刊登国家信息中心预测部的年中经济观察报告称，未来一段时期，总需求稳中趋缓、产能严重过剩、粮食产量和库存双高等因素将抑制物价涨幅；M1快速增长、劳动力成本上升、猪肉价格高位运行等因素将支撑价格上涨。', '1', '1', '2016-08-10 14:26:22', '1');
