INSERT INTO `t_news` VALUES ('6927', '2', '2', '11', '汇宝', '毛毛', '【美国黑白家庭财富相差228年】美国政策研究学会报告显示，若美国政府奉行现有经济政策，美国黑人家庭将需要228年才能积累起和今天白人家庭相等的财富。过去30年美国白人家庭财富平均每年增加约1.8万美元，非洲裔家庭财富平均每年仅增加750美元。', null, null, '【美国黑白家庭财富相差228年】美国政策研究学会报告显示，若美国政府奉行现有经济政策，美国黑人家庭将需要228年才能积累起和今天白人家庭相等的财富。过去30年美国白人家庭财富平均每年增加约1.8万美元，非洲裔家庭财富平均每年仅增加750美元。', '1', '1', '2016-08-10 14:26:23', '1');
