INSERT INTO `t_news` VALUES ('6929', '2', '2', '11', '汇宝', '毛毛', '\nEnergy Management Institute高级合伙人Chirichella：油价需突破50美元/桶以恢复交易员信心当前原油市场仍然是两个方面的对决；一个方面是交易方，其将焦点放在较短期、基本上偏空的数据和信息上；而另一个方面则是投资方，其主要关注偏多的中长期走势。美国原油和布伦特原油均需突破50美元/桶大关，并维持在该价位上，才能令短线的交易员对市场重回升势恢复信心。', null, null, '\n<font color class=\"important-text\">Energy Management Institute高级合伙人Chirichella：油价需突破50美元/桶以恢复交易员信心</font><br>当前原油市场仍然是两个方面的对决；一个方面是交易方，其将焦点放在较短期、基本上偏空的数据和信息上；而另一个方面则是投资方，其主要关注偏多的中长期走势。美国原油和布伦特原油均需突破50美元/桶大关，并维持在该价位上，才能令短线的交易员对市场重回升势恢复信心。', '1', '1', '2016-08-10 14:26:23', '1');
