INSERT INTO `t_news` VALUES ('6958', '2', '2', '11', '汇宝', '毛毛', '\n彭博分析：美国原油库存或将下滑 因供应减少而炼油厂需求增加预计截至8月5日当周EIA美原油库存将出现常规回落，因炼油厂需求增加而供应却在减少；受到汽油需求强劲的推动，炼油厂原油需求较五年同期水平高出5%，而美原油产出同比下滑11%，海外原油进口料减少150万桶/日；但高达5.225亿桶的原油库存仍然处于季节性高位，供应过剩忧虑仍在。', null, null, '\n<font color class=\"important-text\"><b>彭博分析：美国原油库存或将下滑 因供应减少而炼油厂需求增加</b></font><br>预计截至8月5日当周EIA美原油库存将出现常规回落，因炼油厂需求增加而供应却在减少；受到汽油需求强劲的推动，炼油厂原油需求较五年同期水平高出5%，而美原油产出同比下滑11%，海外原油进口料减少150万桶/日；但高达5.225亿桶的原油库存仍然处于季节性高位，供应过剩忧虑仍在。', '1', '1', '2016-08-10 14:26:24', '1');
