INSERT INTO `t_news` VALUES ('7176', '2', '2', '11', '汇宝', '毛毛', '【渣打：美元/人民币或稳在6.7下方】渣打亚洲外汇策略师张敬勤称，中国可能不会容忍人民币进一步贬值，将把美元/人民币暂时保持在6.7元下方；中国将防止这道心理关位因为资本外流而被突破；估计中国6月份非外商直接投资(FDI)导致的资本外流增至430亿美元，5月份为350亿美元。', null, null, '【渣打：美元/人民币或稳在6.7下方】渣打亚洲外汇策略师张敬勤称，中国可能不会容忍人民币进一步贬值，将把美元/人民币暂时保持在6.7元下方；中国将防止这道心理关位因为资本外流而被突破；估计中国6月份非外商直接投资(FDI)导致的资本外流增至430亿美元，5月份为350亿美元。', '1', '1', '2016-08-10 14:26:35', '1');
