INSERT INTO `t_news` VALUES ('7179', '2', '2', '11', '汇宝', '毛毛', '\n汇丰大中华区首席经济学家屈宏斌评7月CPI、PPI数据：CPI同比上涨1.8%符合预期。猪价涨幅趋缓，菜价同比下跌，食品通胀继续走低至3.3%。非食品通胀走高至1.4%，主要受医疗教育等服务价格上涨推动；PPI同比降幅收窄至1.7%，采掘业原材料和加工工业通缩均有缓解，工业部门运行状况略有改善；尽管如此，在外需不振，房地产市场降温，私人部门信心低迷的背景下，经济下行压力仍旧显著，稳增长政策还应持续发力。', null, null, '\n<b>汇丰大中华区首席经济学家屈宏斌评7月CPI、PPI数据：</b><br>CPI同比上涨1.8%符合预期。猪价涨幅趋缓，菜价同比下跌，食品通胀继续走低至3.3%。非食品通胀走高至1.4%，主要受医疗教育等服务价格上涨推动；PPI同比降幅收窄至1.7%，采掘业原材料和加工工业通缩均有缓解，工业部门运行状况略有改善；尽管如此，在外需不振，房地产市场降温，私人部门信心低迷的背景下，经济下行压力仍旧显著，稳增长政策还应持续发力。', '1', '1', '2016-08-10 14:26:35', '1');
